const User = require("../models/users");

const detailUsers = (req, res) => {
  const id = req.params.id;
  User.findById(id)
    .then((user) => {
      if (!user) {
        return res.redirect("/");
      }
      res.render("detailUser", {
        user: user,
      });
    })
    .catch((err) => {
      console.error(err);
      res.redirect("/");
    });
};
module.exports = {
  detailUsers,
};

